				External<span style="letter-spacing:-0.2pt"> </span>CSS<span style="letter-spacing:-0.25pt"> </span>is<span style="letter-spacing:-0.2pt"> </span>used<span style="letter-spacing:-0.1pt"> </span>to<span style="letter-spacing:-0.15pt"> </span>apply<span style="letter-spacing:-0.25pt"> </span>CSS<span style="letter-spacing:-0.25pt"> </span>on<span style="letter-spacing:-0.15pt"> </span>multiple<span style="letter-spacing:-0.15pt"> </span>pages<span style="letter-spacing:-0.2pt"> </span>or<span style="letter-spacing:-0.2pt"> </span>all<span style="letter-spacing:-0.25pt"> </span>pages.<span style="letter-spacing:-0.15pt"> </span>Here,<span style="letter-spacing:-0.2pt"> </span>we<span style="letter-spacing:-0.15pt"> </span>write<span style="letter-spacing:-0.15pt"> </span>all<span style="letter-spacing:-0.15pt"> </span>the <span style="letter-spacing:-0.1pt">CSS</span><span style="letter-spacing:-0.65pt"> </span><span style="letter-spacing:-0.1pt">code</span><span style="letter-spacing:-0.65pt"> </span><span style="letter-spacing:-0.1pt">in</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">a</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">CSS</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">file.</span><span style="letter-spacing:-0.5pt"> </span><span style="letter-spacing:-0.1pt">Its</span><span style="letter-spacing:-0.65pt"> </span><span style="letter-spacing:-0.1pt">extension</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">must</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">be</span><span style="letter-spacing:-0.55pt"> </span><span style="letter-spacing:-0.1pt">.CSS</span><span style="letter-spacing:-0.65pt"> </span><span style="letter-spacing:-0.1pt">for</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">example</span><span style="letter-spacing:-0.6pt"> </span><span style="letter-spacing:-0.1pt">style.css.</span>
